import {motion} from 'framer-motion';
import {useState} from 'react';
import axios from 'axios';

const Footer = ({darkMode}) => {
    let apiurl = 'http://127.0.0.1:8000/portapi/';
    let [message, setMessage] = useState('');
    let [email, setEmail] = useState('');
    const [rec, setRec] = useState(false);
    let [msg, setMsg] = useState('');

    const darkBG = darkMode ? 'bg-gray-900' : 'bg-neutral-100';
    const darkText = darkMode ? 'text-[#D1D2D3]' : 'text-gray-900';
    const darkBorder = darkMode ? 'border-[#D1D2D3]' : 'border-gray-900';


    // --------------------****--------------------
    const handleSubmit = (event) => {
        event.preventDefault()
        console.log(email, message, rec)
        var email_regex = /^\w+([.-]?\w+)*@\w+([.-]?\w+)*(\.\w{2,3})+$/;
        email = email.trim().toLowerCase();
        if (email_regex.test(email)){
            var user_data = {
                "email": email,
                "feedback": message,
                "recruiter": rec ? true : false,
            }
            console.log(rec)
        }
        else{
            return 
        }
        axios.post(`${apiurl}post-feedback/`, user_data)
        .then((res => {
            console.log(res.data);
            rec ? 
            setMsg("Thanks for your feedback! I have mailed you little more info about me") :
            setMsg("Thanks for your Feedback!")
        }))
        .catch(err => console.log(err))
        console.log(user_data);
        setMessage("");
        setEmail("");
        setRec("");
    }
    return (
        <div id="contact" className="border-2 h-[100vh] flex flex-col justify-center items-center snap-center ">
            <div className='border-2 w-[70%] h-[60vh] flex flex-col'>
                <div className="bg-inherit w-full h-[78%] flex flex-col justify-around items-center mx-auto p-4 small:w-full mobile:w-full tablet:w-full transition duration-700">
                    <span className="font-light text-md text-[#D1D2D3] transition duration-500 ease-in-out">{msg}</span>
                    <form className="w-full flex flex-col items-center justify-around space-y-2 p-4" onSubmit={handleSubmit}>
                        <span className="flex">
                            <span className="text-[2.5rem] font-bold text-gray-600 small:text-[1.5rem]">C</span>
                            <span className="text-[2.5rem] font-bold text-gray-600 border-b-2 border-b-rose-200 w-[100px] small:text-[1.5rem] small:w-[60px]">ontact</span>
                        </span>
                        <span className="text-md font-light small:text-[0.7rem] mobile:text-sm">Please provide a feedback or get in touch!</span>
                        <input type="text" value={email} onChange={(e) => setEmail(e.target.value)} placeholder="Email" className="border-2 border-gray-200 outline-none p-2 pl-2  text-md font-normal text-black w-[70%]" required autoFocus/>
                        <textarea value={message} onChange={(e) => setMessage(e.target.value)} placeholder="Message" className="border-2 border-gray-200 outline-none p-2 pl-2 text-md  font-normal text-black w-[70%]" rows="5" required></textarea>
                        <span>
                            <label for="recruiter" className='font-light'>Are you a Recruiter? </label>
                            <input type="checkbox" checked={rec} onChange={() => setRec(!rec)} id="recruiter"></input>
                        </span>
                        <button type="submit" className="p-2 m-2 bg-cyan-700 text-white rounded-lg">Submit</button>
                    </form>
                    <span><i className='fa fa-linkedin'></i></span>
                </div>
            </div>
        </div>
    )
}

export default Footer;


