import {motion, useTransform, useScroll} from 'framer-motion';
import {useRef} from 'react';

function useParallax(value, distance) {
    return useTransform(value, [0, 1], [-distance, distance]);
  }

const Content = () => {
    // const work = useRef(null);
    // const {scrollYProgress} = useScroll({taget: work});
    // const y = useParallax(scrollYProgress, 300);

    const work = useRef(null);
    const { scrollYProgress } = useScroll({ target: work });
    const y = useParallax(scrollYProgress, 300);
    return (
        <>


        <section className='relative border-2 h-[100vh] snap-center flex justify-center items-center'>
            <motion.div ref={work}  style={{y}} transition={{duration:1.5}} className="">
                <span>workexp</span>
            </motion.div>
            <div className='border-2 relative overflow-hidden max-h-[90vh] w-[80%] h-[500px] m-[20px]'>
                <span>workex</span>
            </div>
        </section>

            </>
    )
}

const Workex = () => {
    return (
        <Content />
    )
}

export default Workex;
