import {motion, useTransform, useScroll} from 'framer-motion';
import {useRef} from 'react';

function useParallax(value, distance) {
    return useTransform(value, [0, 1], [-distance, distance]);
  }

const Content = () => {
    // const work = useRef(null);
    // const {scrollYProgress} = useScroll({taget: work});
    // const y = useParallax(scrollYProgress, 300);

    const work = useRef(null);
    const { scrollYProgress } = useScroll({ target: work });
    const y = useParallax(scrollYProgress, 300);
    return (
        <section id="academics" className='relative border-2 h-[100vh] snap-center flex justify-center items-center'>

            <div className='border-2 relative w-full h-full flex flex-row justify-center text-center overflow-scroll px-4 py-2'>
                <div className='w-[49%] flex border'>
                    <span className='w-full border'>1</span>
                </div>
                <div className='w-[2%] flex flex-col border'>
                    <span className='w-full border'>2</span>
                </div>
                <div className='w-[49%] flex flex-col border'>
                    <span className='w-full border'>3</span>
                    <span className='w-full border'>4</span>
                </div>
            </div>

        </section>
    )
}

const Workex = () => {
    return (
        <Content />
    )
}

export default Workex;




