import {motion, useTransform, useScroll, useSpring, MotionValue} from "framer-motion";
import {useEffect, useRef} from 'react';

import img1 from "../images/1.jpeg";
import img2 from "../images/2.jpeg";

// function useParallax(value, distance) {
//     return useTransform(value, [0, 1], [-distance, distance]);
//   }

  function Image({ id }) {
    const ref = useRef(null);
    const { scrollYProgress } = useScroll({ target: ref });
    // const y = useParallax(scrollYProgress, 300);
    const y = useTransform(scrollYProgress, [0,1], [-300, 300]);
    console.log(scrollYProgress, y);
    return (
      <section className="relative h-[100vh] flex justify-center items-center snap-center perspective-[500px]">
        <div ref={ref} className="relative overflow-hidden max-h-[90vh] w-[300px] h-[400px] m-[20px] ">
            <img src={img1} alt="about" className="absolute t-0 r-0 b-0 l-0 w-full h-full"></img>
        </div>
        <motion.h2 style={{ y }}>{`#00${id}`}</motion.h2>
      </section>
    );
  }

const About = () => {
    return (
        <>
            {[1,2].map((id) => <Image id={id} />)}
            {/* <div className="w-[90%] h-full aspect-[16/9] border-2 p-2 mx-auto">
