import Home from './components/Home';
import Navbar from './components/Navbar';
import About from './components/About';
import Projects from './components/Projects';
import Workex from './components/Workex';
import Footer from './components/Footer';

import {motion, useScroll} from 'framer-motion';

import {useEffect, useRef, useState} from 'react';


// const HandleScroll = () => {
//   const progressMap = {};
//   refs.forEach((ref) => {
//     const {scrollYProgress} = useScroll({target:ref});
//     progressMap[ref.current.id] = scrollYProgress.get();
//   })
//   setScrollYProgressMap(HandleScroll);
// }


const LeftBar = ({darkMode, setDarkMode}) => {
  return (
    <div className='fixed top-0 left-0 bottom-0 w-[5%] flex flex-col justify-center overflow-hidden'>
      <div className={`${darkMode ? 'bg-black text-white' : 'bg-neutral-200 text-black'} w-[140px] flex flex-row justify-around rounded-lg -rotate-90 mt-80 pb-[20px]`}>
        <button onClick={() => {setDarkMode(true); localStorage.setItem('dl967', true)}}>Dark <i className="fa fa-moon-o "></i> </button>
        <button onClick={() => {setDarkMode(false); localStorage.setItem('dl967', false)}}>light <i className="fa fa-sun-o"></i> </button>
      </div>
    </div>
  )
}
const RightBar = ({pageactive,setpageactive}) => {
  return(
    <div className='fixed top-0 right-0 bottom-0 w-[5%] flex flex-col justify-center items-center'>
        <a  href="#about" className={`${pageactive === 1 ? 'text-blue-700' : 'font-bold'}`}>1</a>
        <a  href="#projects" className={`${pageactive === 2 ? 'text-blue-700' : 'font-bold'}`}>2</a>
        <a  href="#academics" className={`${pageactive === 3 ? 'text-blue-700' : 'font-bold'}`}>3</a>
        <a  href="#contact" className={`${pageactive === 4 ? 'text-blue-700' : 'font-bold'}`}>4</a>
    </div>
  )
}

function App() {
  const [scrollYProgress, setScrollYProgress] = useState(0);
  const [pageActive, setPageActive] = useState(0);
  const [isTop, setIsTop] = useState(true);


  // -------------[Active NavBar Setup]-------------
  const refs = {
    homeref: useRef(0),
    aboutref: useRef(0),
    projectsref: useRef(0),
    academicsref: useRef(0),
    contactref: useRef(0)
  }
  const HandleScroll = () => {
    const progress = (window.scrollY)/(document.documentElement.scrollHeight - window.innerHeight);
    setScrollYProgress(progress);
  }
  useEffect(() => {
    window.addEventListener('scroll', HandleScroll);
    return () => { window.removeEventListener('scroll', HandleScroll)};
  }, []);

  useEffect(() => {
    const homeRect = refs.homeref.current.getBoundingClientRect();
    const aboutRect = refs.aboutref.current.getBoundingClientRect();
    const projectsRect = refs.projectsref.current.getBoundingClientRect();
    const academicsRect = refs.academicsref.current.getBoundingClientRect();
    const contactRect = refs.contactref.current.getBoundingClientRect();

    if(homeRect.top >=0 && homeRect.top <= window.innerHeight){
      setPageActive(0);
      setIsTop(true);
    }
    else if(aboutRect.top >=0 && aboutRect.top <= window.innerHeight){
      setPageActive(1);
    }
    else if(projectsRect.top >= 0 && projectsRect.top <= window.innerHeight){
      setPageActive(2);
    }
    else if(academicsRect.top >= 0 && academicsRect.top <= window.innerHeight){
      setPageActive(3);
    }
    else if(contactRect.top >= 0 && contactRect.top <= window.innerHeight){
      setPageActive(4);
    }
  });

  // -------------[Dark Mode]-------------
  const [darkMode, setDarkMode] = useState(localStorage.getItem('dl967', null) === 'true' ? true : false);
  const darkModeClasses = darkMode ? "bg-gray-800 text-white" : "bg-white text-red-900 font-bold";


  return (
    <div className={`${darkModeClasses} w-full h-full flex flex-row justify-center items-center`}>
      <LeftBar darkMode={darkMode} setDarkMode={setDarkMode} />
      <div className="border-4 border-red-300 w-[90%]">
        <Navbar pageactive={pageActive} setpageactive={setPageActive}/>
        <div ref={refs.homeref}></div>
        <Home />
        <motion.div />

        <div ref={refs.aboutref}></div>
        <About />

        <div ref={refs.projectsref}></div>
        <Projects />

        <div ref={refs.academicsref}></div>
        <Workex />

        <div ref={refs.contactref}></div>
        <Footer />
      </div>
      <RightBar pageactive={pageActive} setpageactive={setPageActive} />
    </div>

  );
}

export default App;
