import {motion, useTransform, useScroll, useSpring, MotionValue} from "framer-motion";
import {useEffect, useRef} from 'react';

import img1 from "../images/1.jpeg";
import img2 from "../images/2.jpeg";

function useParallax(value, distance) {
    return useTransform(value, [0, 1], [-distance, distance]);
  }

  function Image({ id }) {
    const ref = useRef(null);
    const { scrollYProgress } = useScroll({ target: ref });
    const y = useParallax(scrollYProgress, 300);

    return (
      <section>
        <div ref={ref}>
          <img src={`../images${id}.jpeg`} alt="A London skyscraper" />
        </div>
        <motion.h2 style={{ y }}>{`#00${id}`}</motion.h2>
      </section>
    );
  }

const About = () => {
    return (
        <>
            {[1,2].map((id) => <Image id={id} />)}
            <div className="w-[90%] h-full aspect-[16/9] border-2 p-2 mx-auto">
                <img src={img1} alt="about" className="w-full h-full"></img>
            </div>
            <div className="w-[90%] h-full aspect-[16/9] border-2 p-2 mx-auto">
                <img src={img2} alt="about" className="w-full h-full"></img>
            </div>
        </>
    )
}

export default About;
