import {motion, useTransform, useScroll, useSpring, MotionValue} from "framer-motion";
import {useEffect, useRef} from 'react';

import img1 from "../images/1.jpeg";
import img2 from "../images/preet.jpg";

function useParallax(value, distance) {
    console.log('parallax')
    return useTransform(value, [0, 1], [-distance, distance]);
  }

  function Content() {
    const about = useRef(null);
    const { scrollYProgress } = useScroll({ target: about });
    const y = useParallax(scrollYProgress, 300);
    return (
      <section id="about" className="border-2 relative h-[100vh] flex justify-center items-center snap-center px-4">

        <motion.div ref={about} className="border-2 relative overflow-hidden max-h-[90vh] w-[80%] h-[500px] m-[20px] ">
            <span>A Little More About Me!</span>
        </motion.div>

        <motion.div style={{y}} transition={{duration:1}} className={{}}>
            <img src={img2} alt="about" className="w-full h-full rounded-full"></img>
        </motion.div>
      </section>
    );
  }

const About = () => {
    return (
        <>
        <Content />
        </>
    )
}

export default About;
