import {useEffect, useState} from 'react'; 
import logo from '../logo.svg';
import {motion} from "framer-motion";

function Left(){
    return (
        <div className='bg-red-400'>
            <img src={logo} alt="Logo" className="w-40 h-10"/>
        </div>
    )
}

function Right(pageactive){
    console.log(pageactive.pageactive, typeof(pageactive.pageactive))
    return (
        <div className='w-[60%] border-4 p-2 flex justify-around items-center '>
            <a  href="#about" className={`${pageactive.pageactive === 1 ? 'text-blue-700' : 'font-bold'}`}>About</a>
            <a  href="#projects" className={`${pageactive.pageactive === 2 ? 'text-blue-700' : 'font-bold'}`}>Projects</a>
            <a  href="#academics" className={`${pageactive.pageactive === 3 ? 'text-blue-700' : 'font-bold'}`}>Academics</a>
            <a  href="#contact" className={`${pageactive.pageactive === 4 ? 'text-blue-700' : 'font-bold'}`}>Contact</a>
        </div>
    )
}

function Navbar({pageactive, isTop}){
    const fillPercent = useState(0);
    console.log(isTop);

  // -------------[show/hide nav bar]-------------
    const showNavbar = useState('false');
    const display = isTop ? '-50px' : '0px';

    return (
        <motion.div transition={{duration:1.5}} className="bg-inherit sticky top-0 left-0 right-0 bg-white flex justify-center items-center border-4 p-2 z-40">
            <div style={{top: display}} className="w-[50%] flex justify-center space-x-2 border-4 rounded-full p-2 ">
                <Left />
                <Right pageactive={pageactive}/>
                {pageactive}
            </div>
        </motion.div>
    )
}

export default Navbar;
