import {useEffect, useState} from 'react'; 
// import logo from '../logo.svg';
import logo from '../images/preetham.png';
import {motion} from "framer-motion";

function Left(){
    return (
        <div className='flex justify-center items-center p-2'>
            {/* <img src={logo} alt="Logo" className="w-40 h-10"/> */}
            <span>Preetham.DEV</span>
        </div>
    )
}

function Right({pageactive, isTop}){
    const isTopClassnamesRight = isTop ? 'justify-evenly w-[50%]' : 'w-[60%] justify-evenly';
    console.log(isTop, "fhjbejhb")
    return (
        <div className={`${isTopClassnamesRight} p-2 flex items-center`}>
            <a  href="#about" className={`${pageactive === 1 ? 'text-blue-700' : 'font-light'}`}>About</a>
            <a  href="#projects" className={`${pageactive === 2 ? 'text-blue-700' : 'font-light'}`}>Projects</a>
            <a  href="#academics" className={`${pageactive === 3 ? 'text-blue-700' : 'font-light'}`}>Academics</a>
            <a  href="#contact" className={`${pageactive === 4 ? 'text-blue-700' : 'font-light'}`}>Contact</a>
        </div>
    )
}

function Navbar({pageactive, isTop}){
    const isMobileScreen = {
        outerdiv: 'mobile:w-full mobile:bg-red-300 mobile:text-sm',
        innerdiv: 'mobile:w-full mobile:bg-blue-300',
        innerLeft: '',
        innerRight: ''
    }
  // -------------[If NavBar isTop]-------------
    const isTopClassnames = isTop ? 'w-full justify-between' : 'bg-opacity-10 rounded-full bg-gray-400 text-black shadow-xl';

    return (
        <motion.div transition={{duration:1.5}} className="sticky top-0 left-0 right-0 flex justify-center items-center p-2 z-40">
            <div className={`${isTopClassnames} ${isSmallScreen} relative w-[50%] flex justify-center space-x-2 p-2 `}>
                <Left isTop={isTop}/>
                <Right isTop={isTop} pageactive={pageactive}/>
            </div>
        </motion.div>
    )
}

export default Navbar;
