import {motion, useTransform, useScroll, useSpring, MotionValue} from "framer-motion";
import {useEffect, useRef} from 'react';

import img1 from "../images/1.jpeg";

function useParallax(value, distance) {
    return useTransform(value, [0, 1], [-distance, distance]);
  }

  function Content({projects}) {
    const about = useRef(null);
    const { scrollYProgress } = useScroll({ target: about });
    const y = useParallax(scrollYProgress, 300);
    const p = [];
    projects.forEach((obj) => 
        p.push(
            <>
                <motion.div ref={about} className="border-2 relative overflow-hidden max-h-[90vh] w-[80%] h-[500px] m-[20px] ">
                    <span>{obj.name}</span>
                </motion.div>
                <motion.div style={{y}} transition={{duration:1}} className={{}}>
                    <img src={img1} alt="about" className="w-full h-full"></img>
                </motion.div>
            </>
        )
)
    return (
        <section id="projects" className="border-2 relative h-[100vh] flex flex-col justify-center items-center snap-center">
            {p}
        </section>
        );
  }

  const Projects = ({darkMode}) => {
    const projects = [
        {
            "name": "GeoLocation",
            "Description": "A powerful gesture recognition system for the browser.Motion extends the basic set of event listeners provided by React with a simple yet powerful set of UI gesture recognisers.It currently has support for hover, tap, pan and drag gesture detection. Each gesture has a series of event listeners that you can attach to your motion component.",
            "type": "Website",
            "tech": ["django", "react", "tailwind", "aws"]
        },
        {
            "name": "Rate my Food",
            "Description": "A powerful gesture recognition system for the browser.Motion extends the basic set of event listeners provided by React with a simple yet powerful set of UI gesture recognisers.It currently has support for hover, tap, pan and drag gesture detection. Each gesture has a series of event listeners that you can attach to your motion component.",
            "type": "Website",
            "tech": ["django", "SQL", "react", "tailwind", "aws"]
        },
        {
            "name": "Portfolio",
            "Description": "A powerful gesture recognition system for the browser.Motion extends the basic set of event listeners provided by React with a simple yet powerful set of UI gesture recognisers.It currently has support for hover, tap, pan and drag gesture detection. Each gesture has a series of event listeners that you can attach to your motion component.",
            "type": "Website",
            "tech": ["python", "react", "tailwind", "aws"]
        }
    ]
    return (
        <Content projects={projects} />
    )
  }

  export default Projects;
