import Home from './components/Home';
import Navbar from './components/Navbar';
import About from './components/About';
import Projects from './components/Projects';
import Workex from './components/Workex';
import Footer from './components/Footer';

import {motion, useScroll} from 'framer-motion';

import {useEffect, useRef, useState} from 'react';

const LeftBar = ({pageactive}) => {
  const pageActiveClassnames = 'font-bold text-xl';
  return(
    <>
      <div className='flex flex-col justify-left text-left px-4'>
        <span className='text-[1.8rem] font-bold'>Preetham Bhavirisetty</span>
        <span className='text-[1.1rem] font-normal'>Master's student at University in Binghamton</span>
        <span className='text-[1rem] font-light'>I am driven by the thrill of turning ideas into functional and beautiful software.</span>
      </div>
      <div className='flex flex-col justify-center text-right w-[25%] mb-60'>
        {
          [
            ['About','#about',0],
            ['Projects','#projects',2],
            ['Experience','#academics',3],
            ['Contact','#contact',4]
          ].map(([title,url,page]) => (
          <div className='flex flex-row'>
            <span className={`${pageactive === page ? 'border-t-cyan-800 px-[20px]' : 'px-[10px]'} border-b-0 border-l-0 border-r-0 border-t-2 py-0 mt-[13px] mr-[5px]`}></span> 
            <a  href={url} className={`${pageactive ===  page ? pageActiveClassnames : 'font-light'}`}>{title}</a>
          </div>)
          )
        }
      </div>
      <div className={`${true ? 'bg-black text-white' : 'bg-neutral-200 text-black'} flex flex-row justify-around`}>
          <button onClick={() => {setDarkMode(true); localStorage.setItem('dl967', true)}}>Dark <i className="fa fa-moon-o "></i> </button>
          <button onClick={() => {setDarkMode(false); localStorage.setItem('dl967', false)}}>light <i className="fa fa-sun-o"></i> </button>
      </div>
      <div className='w-[40%] flex justify-around items-center'>
        <i className='fa fa-envelope'></i>
        <i className='fa fa-linkedin'></i>
        <i className='fa fa-github'></i>
      </div>
    </>
  )
}

function App() {
  const [scrollYProgress, setScrollYProgress] = useState(0);
  const [pageActive, setPageActive] = useState(1);
  const [isTop, setIsTop] = useState(true);


  // -------------[Active NavBar Setup]-------------
  const refs = {
    homeref: useRef(0),
    aboutref: useRef(0),
    projectsref: useRef(0),
    academicsref: useRef(0),
    contactref: useRef(0)
  }
  const HandleScroll = () => {
    const progress = (window.scrollY)/(document.documentElement.scrollHeight - window.innerHeight);
    setScrollYProgress(progress);
  }
  useEffect(() => {
    window.addEventListener('scroll', HandleScroll);
    return () => { window.removeEventListener('scroll', HandleScroll)};
  }, []);

  useEffect(() => {
    const homeRect = refs.homeref.current.getBoundingClientRect();
    const aboutRect = refs.aboutref.current.getBoundingClientRect();
    const projectsRect = refs.projectsref.current.getBoundingClientRect();
    const academicsRect = refs.academicsref.current.getBoundingClientRect();
    const contactRect = refs.contactref.current.getBoundingClientRect();

    homeRect.top >= 0 ? setIsTop(true): setIsTop(false);
    console.log(homeRect.top)

    if(homeRect.top >= 0 && homeRect.top <= window.innerHeight){
      setPageActive(0);
    }
    else if(aboutRect.top >= 0 && aboutRect.top <= window.innerHeight){
      setPageActive(1);
    }
    else if(projectsRect.top >= 0 && projectsRect.top <= window.innerHeight){
      setPageActive(2);
    }
    else if(academicsRect.top >= 0 && academicsRect.top <= window.innerHeight){
      setPageActive(3);
    }
    else if(contactRect.top >= 0 && contactRect.top <= window.innerHeight){
      setPageActive(4);
    }
  });

  // -------------[Dark Mode]-------------
  const [darkMode, setDarkMode] = useState(localStorage.getItem('dl967', null) === 'true' ? true : false);
  const darkModeClasses = darkMode ? "bg-gray-800 text-white" : "bg-white text-cyan-900 font-bold";


  return (
    <>
      <div ref={refs.homeref}></div>
      {/* <Home darkMode={darkMode}/> */}
      <div className={`${darkModeClasses} w-full h-full flex flex-row justify-between items-center`}>
        <div className={`fixed top-0 left-0 bottom-0 w-[25%] flex flex-col justify-between items-center py-20`}>
          <LeftBar pageactive={pageActive} />
        </div>

        <div className="w-[75%] ml-[25%]">
          {/* <Navbar darkMode={darkMode} setDarkMode={setDarkMode} pageactive={pageActive} isTop={isTop}/> */}

          <div ref={refs.aboutref}></div>
          <About />

          <div ref={refs.projectsref}></div>
          <Projects />

          <div ref={refs.academicsref}></div>
          <Workex />

          <div ref={refs.contactref}></div>
          <Footer />
        </div>
      </div>
    </>

  );
}

export default App;
