import {useEffect, useState} from 'react'; 
// import logo from '../logo.svg';
import logo from '../images/preetham.png';
import {motion} from "framer-motion";

function Left(){
    return (
        <div className='flex justify-center items-center p-2'>
            {/* <img src={logo} alt="Logo" className="w-40 h-10"/> */}
            <span>Preetham.DEV</span>
        </div>
    )
}

function Right(pageactive){
    return (
        <div className='w-[60%]  p-2 flex items-center '>
            <a  href="#about" className={`${pageactive.pageactive === 1 ? 'text-blue-700' : 'font-light'}`}>About</a>
            <a  href="#projects" className={`${pageactive.pageactive === 2 ? 'text-blue-700' : 'font-light'}`}>Projects</a>
            <a  href="#academics" className={`${pageactive.pageactive === 3 ? 'text-blue-700' : 'font-light'}`}>Academics</a>
            <a  href="#contact" className={`${pageactive.pageactive === 4 ? 'text-blue-700' : 'font-light'}`}>Contact</a>
        </div>
    )
}

function Navbar({pageactive, isTop}){
    const fillPercent = useState(0);
    console.log(isTop);
    const showNavbar = useState('false');

  // -------------[If NavBar isTop]-------------
    const isTopClassnames = isTop ? 'w-full justify-between' : 'bg-opacity-30 rounded-full bg-gray-200 text-black shadow-xl';
    const isTopClassnamesRight = isTop ? '' : '';

    return (
        <motion.div transition={{duration:1.5}} className="sticky top-0 left-0 right-0 flex justify-center items-center p-2 z-40">
            <div className={`${isTopClassnames} relative w-[50%] flex justify-center space-x-2 p-2 `}>
                <Left />
                <Right pageactive={pageactive}/>
            </div>
        </motion.div>
    )
}

export default Navbar;
