import {motion, useTransform, useScroll} from 'framer-motion';
import {useRef} from 'react';

const Content = ({value, distance}) => {
    const useParallex = () => {
        return useTransform(value, [0,1], [-distance, distance]);
    }
    const workex = useRef(null);
    const {scrollProgress} = useScroll({taget: workex});
    const y = useParallex(scrollProgress, 300);
    return (
        <div ref={workex} className='border-2 w-full h-[100vh] flex justify-center items-center'>
            <motion.h2 style={{y}}>Workexp</motion.h2>
            <div className='border-2 h-[80vh] w-[80%]'>
                <span>workex</span>
            </div>
        </div>
    )
}

const Workex = () => {
    return (
        <Content />
    )
}

export default Workex;
