import {motion, useTransform, useScroll, useSpring, MotionValue} from "framer-motion";
import {useEffect, useRef} from 'react';

import img1 from "../images/1.jpeg";
import img2 from "../images/2.jpeg";

function useParallax(value, distance) {
    console.log('parallax')
    return useTransform(value, [0, 1], [-distance, distance]);
  }

  function Content({pageactive, setpageactive}) {
    const aboutref = useRef(null);
    const { scrollYProgress } = useScroll({ target: aboutref });
    const y = useParallax(scrollYProgress, 300);

    useEffect(() => {
        const onScroll = () => {
            const progress = scrollYProgress.get();
            // -------------[Active NavBar Button]--------------
            if (progress >= 0.3 && progress <= 0.9) {
                setpageactive(2);
            }
        }
        window.addEventListener('scroll', onScroll);
        return(() => window.removeEventListener('scroll', onScroll));
    }, [scrollYProgress]);

    return (
      <section id="about" className="border-2 relative h-[100vh] flex justify-center items-center snap-center">
        <motion.div ref={aboutref} className="border-2 relative overflow-hidden max-h-[90vh] w-[80%] h-[500px] m-[20px] ">
            <span>About</span>
        </motion.div>
        <motion.div style={{y}} transition={{duration:1}} className={{}}>
            <img src={img2} alt="about" className="w-full h-full"></img>
        </motion.div>
      </section>
    );
  }

const About = ({pageactive, setpageactive}) => {
    return (
        <>
        <Content pageactive={pageactive} setpageactive={setpageactive}/>
        </>
    )
}

export default About;
